import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as o,f as s,a as _,b as n}from"./app-1479ea04.js";const l={},c=_("h1",{id:"最高人民检察院关于检察工作中具体适用修订刑法第十二条若干问题的通知",tabindex:"-1"},[_("a",{class:"header-anchor",href:"#最高人民检察院关于检察工作中具体适用修订刑法第十二条若干问题的通知","aria-hidden":"true"},"#"),n(" 最高人民检察院关于检察工作中具体适用修订刑法第十二条若干问题的通知")],-1),a=_("p",null,"1997年9月24日 最高人民检察院第八届检察委员会第七十四次会议通过",-1),d=_("p",null,"地方各级人民检察院、各级军事检察院：",-1),i=_("p",null,"根据修订刑法第十二条的规定，现对发生在1997年9月30日以前，1997年10月1日后尚未处理或者正在处理的行为如何适用法律的若干问题通知如下：",-1),r=_("p",null,"如果当时的法律（包括1979年刑法，中华人民共和国惩治军人违反职责罪暂行条例，全国人大常委会关于刑事法律的决定、补充规定，民事、经济、行政法律中“依照”、“比照”刑法有关条款追究刑事责任的法律条文，下同）、司法解释认为是犯罪，修订刑法不认为是犯罪的，依法不再追究刑事责任。已经立案、侦查的，撤销案件；已批准逮捕的，撤销批准逮捕决定，并建议公安机关撤销案件；审查起诉的，作出不起诉决定；已经起诉的，建议人民法院退回案件，予以撤销；已经抗诉的，撤回抗诉。",-1),h=_("p",null,"如果当时的法律、司法解释认为是犯罪，修订刑法也认为是犯罪的，按从旧兼从轻的原则依法追究刑事责任：",-1),u=_("p",null,"罪名、构成要件、情节以及法定刑没有变化的，适用当时的法律追究刑事责任。",-1),p=_("p",null,"罪名、构成要件、情节以及法定刑已经变化的，根据从轻原则，确定适用当时的法律或者修订刑法追究刑事责任。",-1),f=_("p",null,"三、如果当时的法律不认为是犯罪，修订刑法认为是犯罪的，适用当时的法律；但行为连续或者继续到1997年10月1日以后的，对10月1日以后构成犯罪的行为适用修订刑法追究刑事责任。",-1),m=_("p",null,"最高人民检察院",-1),x=_("p",null,"1997年10月6日",-1);function N(B,V){return t(),o("div",null,[c,a,s(" INFO END "),d,i,r,h,u,p,f,m,x])}const v=e(l,[["render",N],["__file","最高人民检察院关于检察工作中具体适用修订刑法第十二条若干问题的通知(1997-10-06).html.vue"]]);export{v as default};
