import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as o,f as s,a as _,b as n}from"./app-aca50503.js";const c={},a=_("h1",{id:"最高人民法院关于人民法院能否对信用证开证保证金采取冻结和扣划措施问题的规定",tabindex:"-1"},[_("a",{class:"header-anchor",href:"#最高人民法院关于人民法院能否对信用证开证保证金采取冻结和扣划措施问题的规定","aria-hidden":"true"},"#"),n(" 最高人民法院关于人民法院能否对信用证开证保证金采取冻结和扣划措施问题的规定")],-1),l=_("p",null,"1996年6月20日 最高人民法院审判委员会第822次会议通过",-1),r=_("p",null,"2020年12月23日 最高人民法院审判委员会第1823次会议通过",-1),d=_("p",null,"信用证开证保证金属于有进出口经营权的企业向银行申请对国外（境外）方开立信用证而备付的具有担保支付性质的资金。为了严肃执法和保护当事人的合法权益，现就有关冻结、扣划信用证开证保证金的问题规定如下：",-1),i=_("p",null,"一、人民法院在审理或执行案件时，依法可以对信用证开证保证金采取冻结措施，但不得扣划。如果当事人、开证银行认为人民法院冻结和扣划的某项资金属于信用证开证保证金的，应当依法提出异议并提供有关证据予以证明。人民法院审查后，可按以下原则处理：对于确系信用证开证保证金的，不得采取扣划措施；如果开证银行履行了对外支付义务，根据该银行的申请，人民法院应当立即解除对信用证开证保证金相应部分的冻结措施；如果申请开证人提供的开证保证金是外汇，当事人又举证证明信用证的受益人提供的单据与信用证条款相符时，人民法院应当立即解除冻结措施。",-1),h=_("p",null,"二、如果银行因信用证无效、过期，或者因单证不符而拒付信用证款项并且免除了对外支付义务，以及在正常付出了信用证款项并从信用证开证保证金中扣除相应款额后尚有剩余，即在信用证开证保证金账户存款已丧失保证金功能的情况下，人民法院可以依法采取扣划措施。",-1),u=_("p",null,"三、人民法院对于为逃避债务而提供虚假证据证明属信用证开证保证金的单位和个人，应当依照民事诉讼法的有关规定严肃处理。",-1);function p(f,m){return t(),o("div",null,[a,l,r,s(" INFO END "),d,i,h,u])}const B=e(c,[["render",p],["__file","最高人民法院关于人民法院能否对信用证开证保证金采取冻结和扣划措施问题的规定(2020-12-29).html.vue"]]);export{B as default};
