import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as o,f as p,a as _,b as a,e as c}from"./app-3fcefb39.js";const s={},r=_("h1",{id:"最高人民法院关于修改《最高人民法院关于仲裁司法审查案件报核问题的有关规定》的决定",tabindex:"-1"},[_("a",{class:"header-anchor",href:"#最高人民法院关于修改《最高人民法院关于仲裁司法审查案件报核问题的有关规定》的决定","aria-hidden":"true"},"#"),a(" 最高人民法院关于修改《最高人民法院关于仲裁司法审查案件报核问题的有关规定》的决定")],-1),n=_("p",null,"2021年11月15日 最高人民法院审判委员会第1850次会议通过",-1),d=_("p",null,"2022年1月1日 施行",-1),i=c("<p>根据审判实践需要，经最高人民法院审判委员会第1850次会议决定，对《最高人民法院关于仲裁司法审查案件报核问题的有关规定》作如下修改：</p><p>一、将第三条修改为：“本规定第二条第二款规定的非涉外涉港澳台仲裁司法审查案件，高级人民法院经审查，拟同意中级人民法院或者专门人民法院以违背社会公共利益为由不予执行或者撤销我国内地仲裁机构的仲裁裁决的，应当向最高人民法院报核，待最高人民法院审核后，方可依最高人民法院的审核意见作出裁定。”</p><p>二、增加一条作为第四条：“依据本规定第二条第二款由高级人民法院审核的案件，高级人民法院应当在作出审核意见之日起十五日内向最高人民法院报备。”</p><p>三、原第四条作为第五条。</p><p>四、原第五条作为第六条。</p><p>五、原第六条作为第七条。</p><p>六、原第七条作为第八条。</p><p>七、原第八条作为第九条。</p><p>本决定自2022年1月1日起施行。</p><p>根据本决定，《最高人民法院关于仲裁司法审查案件报核问题的有关规定》作相应修改后，重新公布。</p>",10);function l(h,f){return t(),o("div",null,[r,n,d,p(" INFO END "),i])}const N=e(s,[["render",l],["__file","最高人民法院关于修改《最高人民法院关于仲裁司法审查案件报核问题的有关规定》的决定.html.vue"]]);export{N as default};
