import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as p,c as t,f as o,a as _,b as a,e as c}from"./app-1479ea04.js";const s={},r=_("h1",{id:"最高人民法院关于确定民事侵权精神损害赔偿责任若干问题的解释",tabindex:"-1"},[_("a",{class:"header-anchor",href:"#最高人民法院关于确定民事侵权精神损害赔偿责任若干问题的解释","aria-hidden":"true"},"#"),a(" 最高人民法院关于确定民事侵权精神损害赔偿责任若干问题的解释")],-1),n=_("p",null,"2001年2月26日 最高人民法院审判委员会第1161次会议通过",-1),d=_("p",null,"2020年12月23日 最高人民法院审判委员会第1823次会议通过",-1),i=c("<p>为在审理民事侵权案件中正确确定精神损害赔偿责任，根据《中华人民共和国民法典》等有关法律规定，结合审判实践，制定本解释。</p><p>第一条 因人身权益或者具有人身意义的特定物受到侵害，自然人或者其近亲属向人民法院提起诉讼请求精神损害赔偿的，人民法院应当依法予以受理。</p><p>第二条 非法使被监护人脱离监护，导致亲子关系或者近亲属间的亲属关系遭受严重损害，监护人向人民法院起诉请求赔偿精神损害的，人民法院应当依法予以受理。</p><p>第三条 死者的姓名、肖像、名誉、荣誉、隐私、遗体、遗骨等受到侵害，其近亲属向人民法院提起诉讼请求精神损害赔偿的，人民法院应当依法予以支持。</p><p>第四条 法人或者非法人组织以名誉权、荣誉权、名称权遭受侵害为由，向人民法院起诉请求精神损害赔偿的，人民法院不予支持。</p><p>第五条 精神损害的赔偿数额根据以下因素确定：</p><p>（一）侵权人的过错程度，但是法律另有规定的除外；</p><p>（二）侵权行为的目的、方式、场合等具体情节；</p><p>（三）侵权行为所造成的后果；</p><p>（四）侵权人的获利情况；</p><p>（五）侵权人承担责任的经济能力；</p><p>（六）受理诉讼法院所在地的平均生活水平。</p><p>第六条 在本解释公布施行之前已经生效施行的司法解释，其内容有与本解释不一致的，以本解释为准。</p>",13);function l(h,f){return p(),t("div",null,[r,n,d,o(" INFO END "),i])}const N=e(s,[["render",l],["__file","最高人民法院关于确定民事侵权精神损害赔偿责任若干问题的解释(2020-12-29).html.vue"]]);export{N as default};
