import{_}from"./plugin-vue_export-helper-c27b6911.js";import{o as p,c as e,e as t}from"./app-3fcefb39.js";const a={},c=t('<h1 id="法释〔2019〕18号关于人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序问题的批复" tabindex="-1"><a class="header-anchor" href="#法释〔2019〕18号关于人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序问题的批复" aria-hidden="true">#</a> 法释〔2019〕18号关于人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序问题的批复</h1><p>最高人民法院 最高人民检察院</p><p>关于人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序问题的批复</p><p>（2019年9月9日最高人民法院审判委员会第1776次会议、2019年9月12日最高人民检察院第十三届检察委员会第二十四次会议通过，自2019年12月6日起施行） 法释〔2019〕18号</p><p>中华人民共和国最高人民法院 中华人民共和国最高人民检察院 公告</p><p>《最高人民法院、最高人民检察院关于人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序问题的批复》已于2019年9月9日由最高人民法院审判委员会第1776次会议、2019年9月12日由最高人民检察院第十三届检察委员会第二十四次会议通过，现予公布，自2019年12月6日起施行。</p><p>最高人民法院 最高人民检察院</p><p>2019年11月25日</p><p>各省、自治区、直辖市高级人民法院、人民检察院，解放军军事法院、军事检察院，新疆维吾尔自治区高级人民法院生产建设兵团分院、新疆生产建设兵团人民检察院：</p><p>近来，部分高级人民法院、省级人民检察院就人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序的问题提出请示。经研究，批复如下：</p><p>人民检察院提起刑事附带民事公益诉讼，应履行诉前公告程序。对于未履行诉前公告程序的，人民法院应当进行释明，告知人民检察院公告后再行提起诉讼。</p><p>因人民检察院履行诉前公告程序，可能影响相关刑事案件审理期限的，人民检察院可以另行提起民事公益诉讼。</p><p>此复。</p>',13),o=[c];function r(n,s){return p(),e("div",null,o)}const h=_(a,[["render",r],["__file","法释〔2019〕18号关于人民检察院提起刑事附带民事公益诉讼应否履行诉前公告程序问题的批复.html.vue"]]);export{h as default};
