import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as o,f as s,a as _,b as n}from"./app-25884b26.js";const c={},l=_("h1",{id:"法释〔2020〕13-号关于内地与香港特别行政区相互执行仲裁裁决的补充安排",tabindex:"-1"},[_("a",{class:"header-anchor",href:"#法释〔2020〕13-号关于内地与香港特别行政区相互执行仲裁裁决的补充安排","aria-hidden":"true"},"#"),n(" 法释〔2020〕13 号关于内地与香港特别行政区相互执行仲裁裁决的补充安排")],-1),a=_("p",null,"2020年11月9日 由最高人民法院审判委员会第1815次会议通过",-1),d=_("p",null,"2020年11月27日 施行",-1),r=_("p",null,"依据《最高人民法院关于内地与香港特别行政区相互执行仲裁裁决的安排》(以下简称《安排》)第十一条的规定，最高人民法院与香港特别行政区政府经协商，作出如下补充安排：",-1),i=_("p",null,"一、《安排》所指执行内地或者香港特别行政区仲裁裁决的程序，应解释为包括认可和执行内地或者香港特别行政区仲裁裁决的程序。",-1),h=_("p",null,"二、将《安排》序言及第一条修改为：“根据《中华人民共和国香港特别行政区基本法》第九十五条的规定，经最高人民法院与香港特别行政区（以下简称香港特区）政府协商，现就仲裁裁决的相互执行问题作出如下安排：",-1),u=_("p",null,"“一、内地人民法院执行按香港特区《仲裁条例》作出的仲裁裁决，香港特区法院执行按《中华人民共和国仲裁法》作出的仲裁裁决，适用本安排。”",-1),p=_("p",null,"三、将《安排》第二条第三款修改为：“被申请人在内地和香港特区均有住所地或者可供执行财产的，申请人可以分别向两地法院申请执行。应对方法院要求，两地法院应当相互提供本方执行仲裁裁决的情况。两地法院执行财产的总额，不得超过裁决确定的数额。”",-1),f=_("p",null,"四、在《安排》第六条中增加一款作为第二款：“有关法院在受理执行仲裁裁决申请之前或者之后，可以依申请并按照执行地法律规定采取保全或者强制措施。”",-1),m=_("p",null,"五、本补充安排第一条、第四条自2020年11月27日起施行，第二条、第三条在香港特别行政区完成有关程序后，由最高人民法院公布施行日期。",-1);function x(N,B){return t(),o("div",null,[l,a,d,s(" INFO END "),r,i,h,u,p,f,m])}const k=e(c,[["render",x],["__file","法释〔2020〕13 号关于内地与香港特别行政区相互执行仲裁裁决的补充安排.html.vue"]]);export{k as default};
