import{_}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as p,e as t}from"./app-7b057b82.js";const r={},a=t('<h1 id="法释〔2021〕11号关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的批复" tabindex="-1"><a class="header-anchor" href="#法释〔2021〕11号关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的批复" aria-hidden="true">#</a> 法释〔2021〕11号关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的批复</h1><p>《最高人民法院关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的批复》已于2021年5月31日由最高人民法院审判委员会第1840次会议通过，现予公布，自2021年6月3日起施行。</p><p>最高人民法院<br> 2021年6月3日</p><p>法释〔2021〕11号</p><p>最高人民法院</p><p>关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的批复</p><p>（2021年5月31日最高人民法院审判委员会<br> 第1840次会议通过，自2021年6月3日起施行）</p><p>上海市高级人民法院：</p><p>你院《关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的请示》（沪高法〔2021〕215号）收悉。经研究，批复如下：</p><p>在知识产权侵权诉讼中，被告提交证据证明原告的起诉构成法律规定的滥用权利损害其合法权益，依法请求原告赔偿其因该诉讼所支付的合理的律师费、交通费、食宿费等开支的，人民法院依法予以支持。被告也可以另行起诉请求原告赔偿上述合理开支。</p>',10),c=[a];function o(n,s){return e(),p("div",null,c)}const h=_(r,[["render",o],["__file","法释〔2021〕11号关于知识产权侵权诉讼中被告以原告滥用权利为由请求赔偿合理开支问题的批复.html.vue"]]);export{h as default};
